[['SSH_USER]\n\nset -euo pipefail\n\nVPS_IP="${1:-}"\nSSH_USER="${2:-$(whoami)}"\nPROJECT_DIR="/Users/milkbot/Projects/Milkbot/Kalshi"\nDEPLOY_DIR="/opt/milkbot"\n\nif [[ -z "$VPS_IP'], ['SSH_USER]"\n    echo "Example: $0 192.168.1.100 ubuntu"\n    exit 1\nfi\n\necho "üöÄ Starting Milkbot deployment to $SSH_USER@$VPS_IP"\n\n# 1. Create deployment package\necho "üì¶ Preparing deployment package..."\ncd "$PROJECT_DIR"\nTEMP_PACKAGE=$(mktemp -d)/milkbot-deploy.tar.gz\n\ntar --exclude=\'.git\' \n    --exclude=\'*.pyc\' \n    --exclude=\'__pycache__\' \n    --exclude=\'.pytest_cache\' \n    --exclude=\'.mypy_cache\' \n    --exclude=\'.coverage\' \n    --exclude=\'htmlcov\' \n    --exclude=\'*.log\' \n    -czf "$TEMP_PACKAGE" .\n\n# 2. Transfer to VPS\necho "üì§ Transferring files..."\nscp "$TEMP_PACKAGE" "$SSH_USER@$VPS_IP:/tmp/"\n\n# 3. Deploy on VPS\necho "‚öôÔ∏è  Deploying on VPS..."\nssh "$SSH_USER@$VPS_IP" "\n    set -e\n    echo \'üîß Setting up deployment...\'\n    sudo mkdir -p $DEPLOY_DIR\n    sudo tar -xzf /tmp/$(basename $TEMP_PACKAGE) -C $DEPLOY_DIR --strip-components=1\n    sudo chown -R milkbot:milkbot $DEPLOY_DIR\n    sudo rm /tmp/$(basename $TEMP_PACKAGE)\n    \n    echo \'üêç Setting up Python environment...\'\n    cd $DEPLOY_DIR\n    sudo -u milkbot python3.11 -m venv venv\n    sudo -u milkbot venv/bin/pip install --upgrade pip\n    sudo -u milkbot venv/bin/pip install -r requirements.txt\n    \n    echo \'üìã Setting up systemd services...\'\n    sudo cp deployment/systemd/*.service /etc/systemd/system/\n    sudo cp deployment/systemd/*.timer /etc/systemd/system/\n    sudo systemctl daemon-reload\n    \n    echo \'‚ö° Deployment complete!\'\n    echo \'Next steps:\'\n    echo \'1. Update /opt/milkbot/.env with your production values\'\n    echo \'2. sudo systemctl enable milkbot-*\'\n    echo \'3. sudo systemctl start milkbot-*\'\n"\n\necho "üéâ Deployment finished!"\necho "‚úÖ Files transferred to $DEPLOY_DIR on your VPS"\necho "üìã Next steps:"\necho "   1. SSH into your VPS"\necho "   2. Update /opt/milkbot/.env with production values"\necho "   3. Enable and start services:"\necho "      sudo systemctl enable milkbot-*"\necho "      sudo systemctl start milkbot-*']]